mapper.update:
UPDATE REVIEW
SET	REVIEW_TITLE = #{reviewTitle}
   ,REVIEW_CONTENT = #{reviewContent}
   ,ORIGIN_NAME = #{originName}
   ,CHANGE_NAME = #{changeName}
WHERE REVIEW_NO = #{reviewNo}
AND STATUS = 'Y'

mapper.select:
SELECT COUNT(TOILET_LIKE_KEY)
FROM TOILET_LIKE
WHERE TOILET_KEY = #{toiletKey} AND USER_KEY = #{userKey}
